if (top.location != location) {
    top.location.href = document.location.href
}
$(function () {
    window.prettyPrint && prettyPrint();
    $(".default-date-picker").datepicker({format: "yyyy-mm", language: "es", autoclose: true, minViewMode: "months"});
    $(".dpd1").datepicker({format: "yyyy-mm-dd", language: "es", autoclose: true});
    $(".dpd2").datepicker({format: "yyyy-mm-dd", language: "es", autoclose: true});
    var b = $(".dpd1").datepicker({
        onRender: function (c) {
            return c.valueOf() < now.valueOf() ? "disabled" : ""
        }
    }).on("changeDate", function (d) {
        if (d.date.valueOf() > a.date.valueOf()) {
            var c = new Date(d.date);
            c.setDate(c.getDate() + 1);
            a.setValue(c)
        }
        b.hide();
        $(".dpd2")[0].focus()
    }).data("datepicker");
    var a = $(".dpd2").datepicker({
        onRender: function (c) {
            return c.valueOf() <= b.date.valueOf() ? "disabled" : ""
        }
    }).on("changeDate", function (c) {
        a.hide()
    }).data("datepicker")
});